import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import EmployeeService from '../services/EmployeeService'
import  "./myStyles.css";


const ListEmployeeComponent = () => {

    const [employees, setEmployees] = useState([])

    useEffect(() => {

        getAllEmployees();
    }, [])

    const getAllEmployees = () => {
        EmployeeService.getAllEmployees().then((response) => {
            setEmployees(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteEmployee = (employeeId) => {
       EmployeeService.deleteEmployee(employeeId).then((response) =>{
        getAllEmployees();

       }).catch(error =>{
           console.log(error);
       })

    }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Employees </h2>
            <Link to = "/add-employee" className = "btn btn-primary mb-2" > Add Employee </Link>

            {/* <table className="table table-bordered table-striped"> */}
                {/* <thead>
                    <th> Employee Id </th>
                    <th> Employee Code </th>
                    <th> Employee First Name </th>
                    <th> Employee Last Name </th>
                    <th> Employee Email Id </th>
                    <th> Actions </th>
                </thead> */}

                <tbody>
                    {
                        employees.map(
                            employee =>
                            <tr key = {employee.id}> 
                     <div className='rect'>
                           <div className='yellow-box'> {employee.id}</div>
                           <div>
                           <p className='name'>{employee.employeeFirstName}</p></div>
                           <div>
                           <p className='emp-code'>TCS1234    ............  balaprbp4@gmail.com</p></div>
                          <div> <p className='location'>SALEM, TN</p></div>
                           {/* <p className='email'>balaprb94@gmail.com</p> */}

                           <td>
                                    <Link className="btn btn-info" to={`/edit-employee/${employee.id}`} >Update</Link>

                                    <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button>

                                    <Link className="btn btn-info" to={`/view-employee/${employee.id}`} >View</Link>
                                </td>
                     </div>

                                {/* <td> <b className='id-yellow'> {employee.id}</b> </td> */}
                                <td> {employee.employeeCode}</td>
                                <td>{employee.employeeFirstName}</td>
                                <td>{employee.employeeLastName}</td>
                                <td>{employee.emailId}</td>
                                <td>
                                    <Link className="btn btn-info" to={`/edit-employee/${employee.id}`} >Update</Link>

                                    <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button>

                                    <Link className="btn btn-info" to={`/view-employee/${employee.id}`} >View</Link>
                                </td>
                            </tr>
                        )
                    }
                </tbody>
            {/* </table> */}


        </div>
    )
}

export default ListEmployeeComponent
