import React, {useState, useEffect} from 'react'
import {Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService'

const AddEmployeeComponent = () => {

    const [employeeFirstName, setEmployeeFirstName] = useState('')
    const [employeeCode,setEmployeeCode]=useState('');
    const [employeeLastName, setEmployeeLastName] = useState('')
    const [emailId, setEmailId] = useState('')

    const [dateOfBirth, setDateOfBirth] = useState('')
        const [gender, setGender] = useState('')
        const [age, setAge] = useState('')
        const [currentAddressLine1, setCurrentAddressLine1] = useState('')
        const [currentAddressLine2, setCurrentAddressLine2] = useState('')
        const [currentCity, setCurrentCity] = useState('')
        const [currentPincode, setCurrentPincode] = useState('')
        const [permanentAddressLine1, setPermanentAddressLine1] = useState('')
        const [permanentAddressLine2, setPermanentAddressLine2] = useState('')
        const [permanentCity, setPermanentCity] = useState('')
        const [permanentPincode, setPermanentPincode] = useState('')
        const [mobile, setMobile] = useState('')
        const [emergencyContactName, setEmergencyContactName] = useState('')
        const [emergencyMobile, setEmergencyMobile] = useState('')

        const [companyMail, setCompanyMail] = useState('')
        const [officePhoneNo, setOfficePhoneNo] = useState('')
        const [officeCity, setOfficeCity] = useState('')
        const [reportingManagerMail, setReportingManagerMail] = useState('')
        const [HR_Name, setHR_Name] = useState('')
        const [previousCompany, setPreviousCompany] = useState('')
        const [previousCompanyJoinDate, setPreviousCompanyJoinDate] = useState('')
        const [previousCompanyEndDate, setPreviousCompanyEndDate] = useState('')
        const [projectCode, setProjectCode] = useState('')
        const [projectStartDate, setProjectStartDate] = useState('')
        const [projectEndDate, setProjectEndDate] = useState('')


        const [panCardNumber, setPanCardNumber] = useState('')
        const [aadharCardNumber, setAadharCardNumber] = useState('')
        const [bankName, setBankName] = useState('')
        const [IFSC_Code, setIFSC_Code] = useState('')
        const [bankBranch, setBankBranch] = useState('')
        const [basicSalary, setBasicSalary] = useState('')
        const [bonus, setBonus] = useState('')
        const [houseRentAllowance, setHouseRentAllowance] = useState('')
        const [medicalAllowance, setMedicalAllowance] = useState('')
        const [travelAllowance, setTravelAllowance] = useState('')
        const [PF, setPF] = useState('')
        const [tax, setTax] = useState('')
        const [CTC, setCTC] = useState('')

    const history = useHistory();
    const {id} = useParams();

    function saveOrUpdateEmployee(e) {
        e.preventDefault();

        const employee = { employeeFirstName, employeeCode,employeeLastName, emailId,
            dateOfBirth,gender,
            age,currentAddressLine1,currentAddressLine2,
            currentCity,currentPincode,
            permanentAddressLine1,permanentAddressLine2,
            permanentCity,permanentPincode,mobile,
            emergencyContactName,emergencyMobile,

            companyMail,officePhoneNo,officeCity,
            reportingManagerMail,HR_Name,previousCompany,previousCompanyJoinDate,
            previousCompanyEndDate,projectCode,projectStartDate,projectEndDate,

            panCardNumber,aadharCardNumber,bankName,
            IFSC_Code,bankBranch,basicSalary,bonus,houseRentAllowance,
            medicalAllowance,travelAllowance,PF,tax,CTC

         };

        if (id) {
            EmployeeService.updateEmployee(id, employee).then((response) => {
                history.push('/employees');
            }).catch(error => {
                console.log(error);
            });

        } else {
            EmployeeService.createEmployee(employee).then((response) => {

                console.log(response.data);

                history.push('/employees');

            }).catch(error => {
                console.log(error);
            });
        }

    }

    useEffect(() => {

        EmployeeService.getEmployeeById(id).then((response) =>{
            setEmployeeFirstName(response.data.employeeFirstName)
            setEmployeeLastName(response.data.employeeLastName)
            setEmployeeCode(response.data.employeeCode)
            setEmailId(response.data.emailId)
        }).catch(error => {
            console.log(error)
        })
    }, [id])

    const title = () => {

        if(id){
            return <h2 className = "text-center">Update Employee</h2>
        }else{
            return <h2 className = "text-center">Add Employee</h2>
        }
    }

    return (
        <div>
           <br /><br />
           <div className = "container">
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                       {
                           title()
                       }
                        <div className = "card-body">
                            <form>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> First Name :</label>
                                    <input
                                        type = "text"
                                        placeholder = "Enter first name"
                                        name = "employeeFirstName"
                                        className = "form-control"
                                        value = {employeeFirstName}
                                        onChange = {(e) => setEmployeeFirstName(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Last Name :</label>
                                    <input
                                        type = "text"
                                        placeholder = "Enter last name"
                                        name = "employeeLastName"
                                        className = "form-control"
                                        value = {employeeLastName}
                                        onChange = {(e) => setEmployeeLastName(e.target.value)}
                                    >
                                    </input>
                                </div>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Employee Code</label>
                                    <input
                                        type = "text"
                                        placeholder = "Enter Employee Code"
                                        name = "employeeCode"
                                        className = "form-control"
                                        value = {employeeCode}
                                        onChange = {(e) => setEmployeeCode(e.target.value)}
                                    >
