import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import EmployeeService from '../services/EmployeeService'
import  "./myStyles.css";


const ListEmployeeComponent = () => {

    const [employees, setEmployees] = useState([])

    useEffect(() => {

        getAllEmployees();
    }, [])

    const getAllEmployees = () => {
        EmployeeService.getAllEmployees().then((response) => {
            setEmployees(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteEmployee = (employeeId) => {
       EmployeeService.deleteEmployee(employeeId).then((response) =>{
        getAllEmployees();

       }).catch(error =>{
           console.log(error);
       })

    }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Employees </h2>
            <Link to = "/add-employee" className = "btn btn-primary mb-2" > Add Employee </Link>

                <p>
                    {
                        employees.map(
                            employee =>
                            <p key = {employee.id}> 
                     <div className='rect'>
                           <div className='yellow-box'> {employee.id}</div>
                           <div>
                           <p className='name'>{employee.employeeFirstName}</p>
                           <p className='emp-code'>{employee.employeeCode}</p>
                           <p className='location'>{employee.currentCity}</p>
                           <p className='email'>{employee.emailId}</p>

                           <div className='actions'> 

                                             <div> <Link className="edit1"  to={`/edit-employee/${employee.id}`}><button className='botton1'>EDIT</button></Link></div>
                                             <div> <button className = "delete" onClick = {() => deleteEmployee(employee.id)}><p >DELETE</p></button></div>
                                             <div> <Link className="view1" to={`/view-employee/${employee.id}`} > <button className='botton1'>VIEW</button></Link></div>

                                              </div>

                           </div>


                     </div>
                                <br></br>


                            </p>
                        )
                    }
                </p>        

        </div>
    )
}

export default ListEmployeeComponent
