import React, { useState } from 'react';

export function MyForm() {
  const [textarea, setTextarea] = useState("This is a text area field");
  const [inputText, setInputText] = useState("This is an input field");

  const handleTextareaChange = (event) => {
    setTextarea(event.target.value);
  };

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  return (
    <form>
      <label>
        Enter text:
        <textarea
          value={textarea}
          onChange={handleTextareaChange}
        />
      </label>
      <p>Entered Text: {textarea}</p>
      
      <label>
        Enter input:
        <textarea
          value={textarea}
          onChange={handleTextareaChange}
        />
      </label>
      <p>Entered Input: {inputText}</p>
    </form>
  );
}

export default MyForm;
