import React, { useState } from 'react';

export function MyForm() {
  const [textarea, setTextarea] = useState("This is a text area field");
  const [secondarea, setSecondarea] = useState("This is a text area field");
  const handleChange = (event) => {
    setTextarea(event.target.value);
  };

  const handleChangeAnother = (event) => {
    setSecondarea(event.target.value);
  };

  return (
    <form>
      <label>
        Enter text:
        <textarea
          value={textarea}
          onChange={handleChange}
        />
      </label>
      <p>Entered Text: {textarea}</p>

      <label>
  Enter text:
  <textarea
    value={secondarea}
    onChange={handleChangeAnother}
  />
</label>
<p>Entered Text: {secondarea}</p>
    </form>


  );
}
export default MyForm;
