import React, { useState, useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

  const AddEmployeeComponent = () => {
  const [employeeFirstName, setEmployeeFirstName] = useState('');
  const [employeeLastName, setEmployeeLastName] = useState('');
  const [employeeCode, setEmployeeCode] = useState('');
  const [emailId, setEmailId] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');

  const [age, setAge] = useState('');
  const [position, setPosition] = useState('');

  const [errors, setErrors] = useState({}); // Validation errors

  const history = useHistory();
  const { id } = useParams();

  // Validation logic
  const validateForm = () => {
    let formErrors = {};

    if (!employeeFirstName || !/^[A-Za-z]+$/.test(employeeFirstName)) {
      formErrors.employeeFirstName = 'First name is required and should contain only letters';
    }

    if (!employeeLastName || !/^[A-Za-z]+$/.test(employeeLastName)) {
      formErrors.employeeLastName = 'Last name is required and should contain only letters';
    }

    if (!employeeCode || !/^[A-Za-z0-9]+$/.test(employeeCode)) {
      formErrors.employeeCode = 'Employee code is required and should be alphanumeric';
    }

    if (!emailId || !/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(emailId)) {
      formErrors.emailId = 'Valid email is required';
    }

    if (!dateOfBirth) {
      formErrors.dateOfBirth = 'Date of birth is required';
    }

    if (!age || age <= 0 || age > 100) {
        formErrors.age = 'Valid age is required (1-100)';
    }

    if (!position) {
        formErrors.position = 'Position is required';
    }

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0; // Return true if no errors
  };

  // Save or Update Employee function
  const saveOrUpdateEmployee = (e) => {
    e.preventDefault();

    const employee = {
      employeeFirstName,
      employeeLastName,
      employeeCode,
      emailId,
      dateOfBirth,

      age,
      position,

    };

    if (validateForm()) {
      if (id) {
        EmployeeService.updateEmployee(id, employee)
          .then((response) => {
            history.push('/employees');
          })
          .catch((error) => {
            console.log(error);
          });
      } else {
        EmployeeService.createEmployee(employee)
          .then((response) => {
            console.log(response.data);
            history.push('/employees');
          })
          .catch((error) => {
            console.log(error);
          });
      }
    }
  };

  useEffect(() => {
    if (id) {
      EmployeeService.getEmployeeById(id)
        .then((response) => {
          setEmployeeFirstName(response.data.employeeFirstName);
          setEmployeeLastName(response.data.employeeLastName);
          setEmployeeCode(response.data.employeeCode);
          setEmailId(response.data.emailId);
          setDateOfBirth(response.data.dateOfBirth);

          setAge(response.data.age);
          setPosition(response.data.position);

        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [id]);

  const title = () => {
    return id ? <h2 className="text-center">Update Employee</h2> : <h2 className="text-center">Add Employee</h2>;
  };

  return (
    <div>
      <br />
      <br />
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            {title()}
            <div className="card-body">
              <form>
                <h3>Personal Details:</h3>

                <div className="form-group mb-2">
                  <label className="form-label">First Name:</label>
                  <input
                    type="text"
                    placeholder="Enter first name"
                    name="employeeFirstName"
                    className="form-control"
                    value={employeeFirstName}
                    onChange={(e) => setEmployeeFirstName(e.target.value)}
                  />
                  {errors.employeeFirstName && <small className="text-danger">{errors.employeeFirstName}</small>}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Last Name:</label>
                  <input
                    type="text"
                    placeholder="Enter last name"
                    name="employeeLastName"
                    className="form-control"
                    value={employeeLastName}
                    onChange={(e) => setEmployeeLastName(e.target.value)}
                  />
                  {errors.employeeLastName && <small className="text-danger">{errors.employeeLastName}</small>}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Employee Code:</label>
                  <input
                    type="text"
                    placeholder="Enter employee code"
                    name="employeeCode"
                    className="form-control"
                    value={employeeCode}
                    onChange={(e) => setEmployeeCode(e.target.value)}
                  />
                  {errors.employeeCode && <small className="text-danger">{errors.employeeCode}</small>}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Personal Email ID:</label>
                  <input
                    type="email"
                    placeholder="Enter email Id"
                    name="emailId"
                    className="form-control"
                    value={emailId}
                    onChange={(e) => setEmailId(e.target.value)}
                  />
                  {errors.emailId && <small className="text-danger">{errors.emailId}</small>}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Date of Birth:</label>
                  <input
                    type="date"
                    placeholder="Enter Date of Birth"
                    name="dateOfBirth"
                    className="form-control"
                    value={dateOfBirth}
                    onChange={(e) => setDateOfBirth(e.target.value)}
                  />
                  {errors.dateOfBirth && <small className="text-danger">{errors.dateOfBirth}</small>}
                </div>

                <div className="form-group mb-2">
                                    <label className="form-label">Age</label>
                                    <input
                                        type="number"
                                        placeholder="Enter Age"
                                        name="age"
                                        className="form-control"
                                        value={age}
                                        onChange={(e) => setAge(e.target.value)}
                                    />
                                    {errors.age && <small className="text-danger">{errors.age}</small>}
                                </div>

                                <div className="form-group mb-2">
                                    <label className="form-label">Position</label>
                                    <input
                                        type="text"
                                        placeholder="Enter Position"
                                        name="position"
                                        className="form-control"
                                        value={position}
                                        onChange={(e) => setPosition(e.target.value)}
                                    />
                                    {errors.position && <small className="text-danger">{errors.position}</small>}
                                </div>



                <button className="btn btn-success" onClick={(e) => saveOrUpdateEmployee(e)}>
                  {id ? 'Update' : 'Submit'}
                </button>
                <Link to="/employees" className="btn btn-danger" style={{ marginLeft: '10px' }}>
                  Cancel
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployeeComponent;
