import './edhaya-styles.css';
import 'bootstrap/dist/css/bootstrap.css';
import EmployeeService from '../services/EmployeeService';
import {useParams} from 'react-router-dom';
import React, { useState } from 'react';

import ListEmployeeComponent from './ListEmployeeComponent';

const Login = () => {

    const [password, setPassword] = useState('')
    const [employeeCode,setEmployeeCode]=useState('');
    const {id} = useParams();
    const employee = { password, employeeCode };


    useEffect(() => {

        EmployeeService.getEmployeeById(id).then((response) =>{
            
            setEmployeeCode(response.data.employeeCode)
            setPassword(response.data.password)
        }).catch(error => {
            console.log(error)
        })
    }, [id])
return (
    <>
    <form onSubmit={handleSubmit}>   
    <div id="main" class="container d-flex justify-content-center align-items-center">
    
     <div class="row mb-3">
 
         <label class="col-sm-4" class="form-control-sm form-label">EmployeeCode</label>
         <input class="col-sm-6" class="form-control-sm form-input"  
         type="text"
 
         value={employeeCode}
 
         onChange={(e) => setUsername(e.target.value)}
 
      
         />
     
     </div>
     <div class="row mb-3">
         <label class="col-sm-4" class="form-control-sm form-label">Your Email ID</label>
         <input class="col-sm-6" class="form-control-sm form-input" 
     type="password"
 
     value={password}
 
     onChange={(e) => setPassword(e.target.value)}
 
     />
     </div>
     {error && <p>{error}</p>}
     <div class="mt-3" >
     <button  align="center" type="submit" class="btn btn-primary">Submit</button>
     </div>
 </div>
 </form>  
 </>

);

};

export default Login;
