import axios from "axios";
import React, {useState} from "react";
import "./App.css";

function AxiosUpdate(){
    const initialData = {fname: "", lname:""};
    const [inputData, setInputData] = useState(initialData);

    const handleData = (e)=> {
        setInputData({...inputData,[e.target.value]: [e.target.value]});
    }

    const handleUpdate = (e)=>{
        e.preventDefault();
        axios.put("https://jsonplaceholder.typicode.com/users/1", inputData)
            .then((response) =>{
                console.log(response);
            })
            .catch ((error) => {
                console.error("Error: ", error);
                
            });
    }
    return(
        <div className="container">
        <div className="input-container">
            <label>FIRST NAME :      </label>
            <input type="text" name="fname"
            value={inputData.fname} onChange={handleData}/>
        </div>

        <div className="input-container">
            <label>LAST NAME :      </label>
            <input type="text" name="lname"
            value={inputData.lname} onChange={handleData}/>
        </div>

        <div>
            <button onClick={handleUpdate}> SUBMIT</button>
        </div>

    </div>

    );
    
}

export default AxiosUpdate;
