//New Login


import './Component.css';
import React, {useState, useEffect} from 'react'
import {Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService'



const Login = () => {

const [employeeCode, setEmployeeCode] = useState('');
const [password, setPassword] = useState('');

const HandleSubmit=(e)=>
{

e.preventDefault();

const employee={employeeCode,password}

const history = useHistory();

EmployeeService.login(employee).then((response) => {

    console.log(response.data);

    history.push('/employees');

}).catch(error => {
    console.log(error);
});
}

return (
    <>
    <form onSubmit={(e)=>HandleSubmit(e)}>   
    <div id="main" class="container d-flex justify-content-center align-items-center">
    
     <div class="row mb-3">
 
         <label class="col-sm-4" class="form-control-sm form-label">EmployeeCode</label>
         <input class="col-sm-6" class="form-control-sm form-input"  
         type="text"
 
         value={employeeCode}
 
         onChange={(e) => setEmployeeCode(e.target.value)}  />
     
     </div>
     <div class="row mb-3">
         <label class="col-sm-4" class="form-control-sm form-label">Your Email ID</label>
         <input class="col-sm-6" class="form-control-sm form-input" 
     type="password"
 
     value={password}
 
     onChange={(e) => setPassword(e.target.value)}
 
     />
     </div>
     {error && <p>{error}</p>}
     <div class="mt-3" >
     <button  align="center" type="submit" class="btn btn-primary">Submit</button>
     </div>
 </div>
 </form>  
 </>

);

};

export default Login;
