import React, {useState} from "react";
import * as yup from 'yup';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Await } from "react-router-dom";

function App(){
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [mobile, setMobile] = useState('');
    const [age, setAge] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [error, setErrors] = useState({});

    const userSchema = yup.object().shape({
        firstName: yup.string().required('First Name is requires ***'),
        lastName: yup.string(),
        mobile: yup.string().matches(/^\d{10}$/, 'Mobile Number must be 10 digit').required('Mobile Number is required ***'),
        age: yup.number().typeError('Age must be a Number').integer('Age Must Be An Integer').min(18, 'Age must be at least 18').max(80, 'Ahe must be atmost 80').required('First Name is requires ***'),
        email: yup.string().email('Invalid Email***').required('Email is mandatory ***'),
        password: yup.string().min(8, 'Password must be 8 Character').required('Password is required ***'),
    });

    async function validateForm() {
        try{
            await userSchema.validate({
        firstName,
        lastName,
        mobile,
        age,
        email,
        password,
            }, {abortEarly: false});

            setErrors({}); //clear any previous errors
            alert('Form is Valid');
        }
        catch(error){
            const errorMessages={};
            error.inner.forEach((e) =>{
                errorMessages[e.path]=e.message;
            });
            setErrors(errorMessages);
        }
    }

    return(
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <form>
                        <div className="mb-3">
                            <label htmlFor="firstName" className="form-label">FIRST NAME:</label>
                            <input type="text" className="form-control"
                            id="firstName" value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}
                            />
                            <div className="text-danger">{error.firstName}</div>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="lastName" className="form-label">LAST NAME:</label>
                            <input type="text" className="form-control"
                            id="lastName" value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                            />
                            <div className="text-danger">{error.lastName}</div>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="mobile" className="form-label">MOBILE:</label>
                            <input type="text" className="form-control"
                            id="mobile" value={mobile}
                            onChange={(e) => setMobile(e.target.value)}
                            />
                            <div className="text-danger">{error.mobile}</div>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="age" className="form-label">AGE:</label>
                            <input type="text" className="form-control"
                            id="age" value={age}
                            onChange={(e) => setAge(e.target.value)}
                            />
                            <div className="text-danger">{error.age}</div>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="email" className="form-label">E-MAIL:</label>
                            <input type="text" className="form-control"
                            id="email" value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            />
                            <div className="text-danger">{error.email}</div>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="password" className="form-label">PASSWORD:</label>
                            <input type="password" className="form-control"
                            id="password" value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            />
                            <div className="text-danger">{error.password}</div>
                        </div>

                        <button type="button" className="btn btn-primary"
                        onClick={validateForm}>
                            SUBMIT </button>
                    </form>
                </div>
            </div>
        </div>
    );
}
