import './Component.css';
import 'bootstrap/dist/css/bootstrap.css';
import EmployeeService from '../services/EmployeeService';
import ReactDOM from 'react-dom';

import React, {useState, useEffect} from 'react';

import ListEmployeeComponent from './ListEmployeeComponent';
import ViewEmployeeComponent from './ViewEmployeeComponent';
import { Link } from 'react-router-dom/cjs/react-router-dom';

const Login = () => {

    const [password, setPassword] = useState('')
    const [employeeCode,setEmployeeCode]=useState('');
    const [id,setId]=useState('');
    const [position,setPosition]=useState('');
    const [error,setError]=useState('');
    const HandleSubmit = async (e) => {

        e.preventDefault();
        setError('');  

        const employee = { password, employeeCode ,id,position};
        useEffect(() => {
   
            EmployeeService.login(employee).then((response) =>{
                
                setEmployeeCode(response.data.employeeCode)
                setPassword(response.data.password)
                setId(response.data.id)
                setPosition(response.data.position)

                if ("Admin".equals(response.data.position)) {
                    const root = ReactDOM.createRoot(document.getElementById('root')); 
                    root.render(<ListEmployeeComponent/>); 
                                
                } else if ("Employee".equals(response.data.position)){
                
                    const root = ReactDOM.createRoot(document.getElementById('root')); 
                    root.render(<ViewEmployeeComponent id={response.data.id}/>); 
                
                }
                
                
                if(response.status===200)
                {

                    setError("Invalid Credentials")
                    

                }
                
            }, []).catch(error => {
                console.log(error)
            })
        })

    }

   

return (
    <>
    <form>   
    <div id="main" class="container d-flex justify-content-center align-items-center">
    
     <div class="row mb-3">
 
         <label class="col-sm-4" class="form-control-sm form-label">EmployeeCode</label>
         <input class="col-sm-6" class="form-control-sm form-input"  
         type="text"
 
         value={employeeCode}
 
         onChange={(e) => setEmployeeCode(e.target.value)}
 
      
         />
     
     </div>
     <div class="row mb-3">
         <label class="col-sm-4" class="form-control-sm form-label">Your Email ID</label>
         <input class="col-sm-6" class="form-control-sm form-input" 
     type="password"
 
     value={password}
 
     onChange={(e) => setPassword(e.target.value)}
 
     />
     </div>
     
     <div class="mt-3" >
     <button className = "btn btn-success" onClick = {(e) => HandleSubmit(e)} >Submit </button>
     </div>
 </div>
 </form>  
 </>

);

};

export default Login;
