import React, { useState } from "react";
import './Component.css';
import { Link, useHistory } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
import 'bootstrap/dist/css/bootstrap.min.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();
    const employeeLogin = { username, password };

    const HandleSubmit = (e) => {
        e.preventDefault();
        EmployeeService.login(employeeLogin).then((response) => {
            if (response.data.position === "Admin") {
                history.push("/employees");
            } else {
                console.log("Logged in by : " + response.data.position);
                history.push("/view-employee/" + response.data.id);
            }
        }).catch(error => {
            console.log(error);
        });
    }

    return (
        <>
            <div className="container d-flex justify-content-center align-items-center" style={{ height: '100vh', background: 'rgba(0,0,0,0.6)' }}>
                <div className="login-card p-4 rounded" style={{ background: 'rgba(255, 255, 255, 0.8)', width: '400px' }}>
                    <h2 className="text-center mb-4" style={{ color: '#17a2b8' }}>Login</h2>
                    <form>
                        <div className="form-group mb-3">
                            <label className="form-label" style={{ color: '#343a40' }}>Employee Code</label>
                            <input
                                className="form-control"
                                type="text"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                placeholder="Enter Employee Code"
                                style={{ border: '2px solid #17a2b8' }}
                            />
                        </div>
                        <div className="form-group mb-3">
                            <label className="form-label" style={{ color: '#343a40' }}>Password</label>
                            <input
                                className="form-control"
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Enter Password"
                                style={{ border: '2px solid #17a2b8' }}
                            />
                        </div>
                        <div className="text-center">
                            <Link className="btn btn-info btn-block" onClick={(e) => HandleSubmit(e)} to="" style={{ backgroundColor: '#17a2b8', border: 'none', fontSize: '18px' }}>Submit</Link>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
}

export default Login;
