import './edhaya-styles.css';
import 'bootstrap/dist/css/bootstrap.css';
import React, { useState } from 'react'; import axios from 'axios';
import ReactDOM from 'react-dom/client';

import Admin from './Admin';

const Login = () => {

const [employeeCode, setUsername] = useState('');

const [password, setPassword] = useState('');

const [error, setError] = useState('');

const handleSubmit = async (e) => {

e.preventDefault();
setError('');

try {

const response = await axios.post('http://localhost:8080/login', { employeeCode, password

}); 

if (response.status === 200) {

    
    const root = ReactDOM.createRoot(document.getElementById('root')); 
    root.render(<Admin/>); 



} else {

setError('Invalid credentials');

}

} catch (error) {

setError('Error logging in. Please try again.');

}

};
return (
    <>
    <form onSubmit={handleSubmit}>   
    <div id="main" class="container d-flex justify-content-center align-items-center">
    
     <div class="row mb-3">
 
         <label class="col-sm-4" class="form-control-sm form-label">EmployeeCode</label>
         <input class="col-sm-6" class="form-control-sm form-input"  
         type="text"
 
         value={employeeCode}
 
         onChange={(e) => setUsername(e.target.value)}
 
      
         />
     
     </div>
     <div class="row mb-3">
         <label class="col-sm-4" class="form-control-sm form-label">Your Email ID</label>
         <input class="col-sm-6" class="form-control-sm form-input" 
     type="password"
 
     value={password}
 
     onChange={(e) => setPassword(e.target.value)}
 
     />
     </div>
     {error && <p>{error}</p>}
     <div class="mt-3" >
     <button  align="center" type="submit" class="btn btn-primary">Submit</button>
     </div>
 </div>
 </form>  
 </>

);

};

export default Login;
