import axios from "axios";

const EMPLOYEE_BASE_REST_API_URL = "http://localhost:8080/api/v1/employees";

class EmpServices {
    getAllEmployees() {
        // Return the axios promise with data extraction and error handling
        return axios.get(EMPLOYEE_BASE_REST_API_URL)
            .then(response => response.data)  // Extract data from response
            .catch(error => {
                console.error("There was an error retrieving the employees!", error);
                throw error;  // Re-throw the error after logging it
            });
    }
}

export default new EmpServices();
