import React from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import './Component.css';
import { Container, Row, Col, Nav } from 'react-bootstrap';

const ViewEmployeeComponent = () => {
    const { id } = useParams();

    return (
        <Container fluid className="p-0">
            <Row>
                {/* Sidebar */}<div className='nav-balc'>
                <Col md={3} className="bg-dark text-white vh-100 p-4" >
                    <h4 className="mb-4">Employee Dashboard</h4>
                    <Nav className="flex-column">
                        <Nav.Item className="mb-3">
                            <Link className="nav-link text-white" to={`/view-employee-personal/${id}`}>
                                Personal
                            </Link>
                        </Nav.Item>
                        <Nav.Item className="mb-3">
                            <Link className="nav-link text-white" to={`/view-employee-professional/${id}`}>
                                Professional
                            </Link>
                        </Nav.Item>
                        <Nav.Item className="mb-3">
                            <Link className="nav-link text-white" to={`/view-employee-finance/${id}`}>
                                Finance
                            </Link>
                        </Nav.Item>
                    </Nav>
                </Col>
                </div>

                {/* Main Content */}
                <Col md={9} className="p-4">
                    <h2>Welcome to the Employee Dashboard</h2>
                    <p>Select a section from the sidebar to view detailed employee information.</p>
                </Col>
            </Row>
        </Container>
    );
}

export default ViewEmployeeComponent;
