import React, { useState, useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const AddEmployeeComponent = () => {
  const [employeeFirstName, setEmployeeFirstName] = useState('');
  const [employeeCode, setEmployeeCode] = useState('');
  const [employeeLastName, setEmployeeLastName] = useState('');
  const [emailId, setEmailId] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');

  const [errors, setErrors] = useState({}); // To store validation errors

  const history = useHistory();
  const { id } = useParams();

  // Validation logic
  const validateForm = () => {
    let formErrors = {};

    if (!employeeFirstName.trim()) {
      formErrors.employeeFirstName = 'First name is required';
    }
    if (!employeeLastName.trim()) {
      formErrors.employeeLastName = 'Last name is required';
    }
    if (!employeeCode.trim()) {
      formErrors.employeeCode = 'Employee code is required';
    }
    if (!emailId.trim()) {
      formErrors.emailId = 'Email ID is required';
    } else if (!/\S+@\S+\.\S+/.test(emailId)) {
      formErrors.emailId = 'Email format is invalid';
    }
    if (!dateOfBirth.trim()) {
      formErrors.dateOfBirth = 'Date of birth is required';
    }

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0; // Return true if no errors
  };

  const saveOrUpdateEmployee = (e) => {
    e.preventDefault();

    const employee = {
      employeeFirstName,
      employeeCode,
      employeeLastName,
      emailId,
      dateOfBirth,
    };

    if (validateForm()) { // Check validation before submitting
      if (id) {
        EmployeeService.updateEmployee(id, employee)
          .then((response) => {
            history.push('/employees');
          })
          .catch((error) => {
            console.log(error);
          });
      } else {
        EmployeeService.createEmployee(employee)
          .then((response) => {
            console.log(response.data);
            history.push('/employees');
          })
          .catch((error) => {
            console.log(error);
          });
      }
    }
  };

  useEffect(() => {
    if (id) {
      EmployeeService.getEmployeeById(id)
        .then((response) => {
          setEmployeeFirstName(response.data.employeeFirstName);
          setEmployeeLastName(response.data.employeeLastName);
          setEmployeeCode(response.data.employeeCode);
          setEmailId(response.data.emailId);
          setDateOfBirth(response.data.dateOfBirth);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [id]);

  const title = () => {
    return id ? (
      <h2 className="text-center">Update Employee</h2>
    ) : (
      <h2 className="text-center">Add Employee</h2>
    );
  };

  return (
    <div>
      <br />
      <br />
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            {title()}
            <div className="card-body">
              <form>
                <h3>Personal Details:</h3>

                <div className="form-group mb-2">
                  <label className="form-label">First Name:</label>
                  <input
                    type="text"
                    placeholder="Enter first name"
                    name="employeeFirstName"
                    className="form-control"
                    value={employeeFirstName}
                    onChange={(e) => setEmployeeFirstName(e.target.value)}
                  />
                  {errors.employeeFirstName && (
                    <small className="text-danger">
                      {errors.employeeFirstName}
                    </small>
                  )}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Last Name:</label>
                  <input
                    type="text"
                    placeholder="Enter last name"
                    name="employeeLastName"
                    className="form-control"
                    value={employeeLastName}
                    onChange={(e) => setEmployeeLastName(e.target.value)}
                  />
                  {errors.employeeLastName && (
                    <small className="text-danger">
                      {errors.employeeLastName}
                    </small>
                  )}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Employee Code:</label>
                  <input
                    type="text"
                    placeholder="Enter employee code"
                    name="employeeCode"
                    className="form-control"
                    value={employeeCode}
                    onChange={(e) => setEmployeeCode(e.target.value)}
                  />
                  {errors.employeeCode && (
                    <small className="text-danger">
                      {errors.employeeCode}
                    </small>
                  )}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Email ID:</label>
                  <input
                    type="email"
                    placeholder="Enter email ID"
                    name="emailId"
                    className="form-control"
                    value={emailId}
                    onChange={(e) => setEmailId(e.target.value)}
                  />
                  {errors.emailId && (
                    <small className="text-danger">{errors.emailId}</small>
                  )}
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Date of Birth:</label>
                  <input
                    type="date"
                    placeholder="Enter date of birth"
                    name="dateOfBirth"
                    className="form-control"
                    value={dateOfBirth}
                    onChange={(e) => setDateOfBirth(e.target.value)}
                  />
                  {errors.dateOfBirth && (
                    <small className="text-danger">{errors.dateOfBirth}</small>
                  )}
                </div>

                <button
                  className="btn btn-success"
                  onClick={(e) => saveOrUpdateEmployee(e)}
                >
                  Submit
                </button>
                <Link to="/employees" className="btn btn-danger">
                  Cancel
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployeeComponent;
