import React, { useState, useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
import 'bootstrap/dist/css/bootstrap.min.css';

const AddEmployeeComponent = () => {
    const [employeeFirstName, setEmployeeFirstName] = useState('');
    const [employeeLastName, setEmployeeLastName] = useState('');
    const [employeeCode, setEmployeeCode] = useState('');
    const [emailId, setEmailId] = useState('');
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [age, setAge] = useState('');
    const [position, setPosition] = useState('');
    const [errors, setErrors] = useState({});
    const history = useHistory();
    const { id } = useParams();

    const validateForm = () => {
        let formErrors = {};
        if (!employeeFirstName || !/^[A-Za-z]+$/.test(employeeFirstName)) {
            formErrors.employeeFirstName = 'First name is required and should contain only letters';
        }
        if (!employeeLastName || !/^[A-Za-z]+$/.test(employeeLastName)) {
            formErrors.employeeLastName = 'Last name is required and should contain only letters';
        }
        if (!employeeCode || !/^[A-Za-z0-9]+$/.test(employeeCode)) {
            formErrors.employeeCode = 'Employee code is required and should be alphanumeric';
        }
        if (!emailId || !/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(emailId)) {
            formErrors.emailId = 'Valid email is required';
        }
        if (!dateOfBirth) {
            formErrors.dateOfBirth = 'Date of birth is required';
        }
        if (!age || age <= 0 || age > 100) {
            formErrors.age = 'Valid age is required (1-100)';
        }
        if (!position) {
            formErrors.position = 'Position is required';
        }
        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    const saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        const employee = { employeeFirstName, employeeLastName, employeeCode, emailId, dateOfBirth, age, position };
        if (validateForm()) {
            if (id) {
                EmployeeService.updateEmployee(id, employee)
                    .then(() => {
                        history.push('/employees');
                    })
                    .catch((error) => console.log(error));
            } else {
                EmployeeService.createEmployee(employee)
                    .then(() => {
                        history.push('/employees');
                    })
                    .catch((error) => console.log(error));
            }
        }
    };

    useEffect(() => {
        if (id) {
            EmployeeService.getEmployeeById(id)
                .then((response) => {
                    setEmployeeFirstName(response.data.employeeFirstName);
                    setEmployeeLastName(response.data.employeeLastName);
                    setEmployeeCode(response.data.employeeCode);
                    setEmailId(response.data.emailId);
                    setDateOfBirth(response.data.dateOfBirth);
                    setAge(response.data.age);
                    setPosition(response.data.position);
                })
                .catch((error) => console.log(error));
        }
    }, [id]);

    const title = () => (
        <h2 className="text-center mb-4">
            {id ? 'Update Employee' : 'Add Employee'}
        </h2>
    );

    return (
        <div className="container my-5">
            <div className="row justify-content-center">
                <div className="col-md-8">
                    <div className="card shadow-lg p-4">
                        {title()}
                        <div className="card-body">
                            <form>
                                <h4 className="text-primary mb-4">Personal Details:</h4>

                                <div className="mb-3">
                                    <label className="form-label">First Name:</label>
                                    <input
                                        type="text"
                                        placeholder="Enter first name"
                                        className={`form-control ${errors.employeeFirstName && 'is-invalid'}`}
                                        value={employeeFirstName}
                                        onChange={(e) => setEmployeeFirstName(e.target.value)}
                                    />
                                    {errors.employeeFirstName && <div className="invalid-feedback">{errors.employeeFirstName}</div>}
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Last Name:</label>
                                    <input
                                        type="text"
                                        placeholder="Enter last name"
                                        className={`form-control ${errors.employeeLastName && 'is-invalid'}`}
                                        value={employeeLastName}
                                        onChange={(e) => setEmployeeLastName(e.target.value)}
                                    />
                                    {errors.employeeLastName && <div className="invalid-feedback">{errors.employeeLastName}</div>}
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Employee Code:</label>
                                    <input
                                        type="text"
                                        placeholder="Enter employee code"
                                        className={`form-control ${errors.employeeCode && 'is-invalid'}`}
                                        value={employeeCode}
                                        onChange={(e) => setEmployeeCode(e.target.value)}
                                    />
                                    {errors.employeeCode && <div className="invalid-feedback">{errors.employeeCode}</div>}
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Email ID:</label>
                                    <input
                                        type="email"
                                        placeholder="Enter email"
                                        className={`form-control ${errors.emailId && 'is-invalid'}`}
                                        value={emailId}
                                        onChange={(e) => setEmailId(e.target.value)}
                                    />
                                    {errors.emailId && <div className="invalid-feedback">{errors.emailId}</div>}
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Date of Birth:</label>
                                    <input
                                        type="date"
                                        className={`form-control ${errors.dateOfBirth && 'is-invalid'}`}
                                        value={dateOfBirth}
                                        onChange={(e) => setDateOfBirth(e.target.value)}
                                    />
                                    {errors.dateOfBirth && <div className="invalid-feedback">{errors.dateOfBirth}</div>}
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Age:</label>
                                    <input
                                        type="number"
                                        placeholder="Enter age"
                                        className={`form-control ${errors.age && 'is-invalid'}`}
                                        value={age}
                                        onChange={(e) => setAge(e.target.value)}
                                    />
                                    {errors.age && <div className="invalid-feedback">{errors.age}</div>}
                                </div>

                                <div className="mb-4">
                                    <label className="form-label">Position:</label>
                                    <input
                                        type="text"
                                        placeholder="Enter position"
                                        className={`form-control ${errors.position && 'is-invalid'}`}
                                        value={position}
                                        onChange={(e) => setPosition(e.target.value)}
                                    />
                                    {errors.position && <div className="invalid-feedback">{errors.position}</div>}
                                </div>

                                <div className="d-flex justify-content-between">
                                    <button
                                        className="btn btn-success px-4"
                                        onClick={saveOrUpdateEmployee}
                                    >
                                        {id ? 'Update' : 'Submit'}
                                    </button>
                                    <Link to="/employees" className="btn btn-danger px-4">
                                        Cancel
                                    </Link>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddEmployeeComponent;
