import React, {useState} from "react";
import Item from "./Item";

const ItemList = () => {
   const [enteredItems, setEnteredItems] = useState([]);
   const [newItem, setNewItem] = useState('');

   const handleInputChange = (e) =>{
    setNewItem(e.target.value);
   };

   const handleAddItem = () =>{
    if (newItem.trim() !== " "){
        setEnteredItems([...enteredItems, newItem]);
        setNewItem('');
    }
   };

   return(
    <>
    <h1>ENTERED ITEMS ARE LISTED...</h1>
    <div>
        <input type="text" placeholder="ENTER YOUR NEEDED"
        value={newItem} onChange={handleInputChange}/>
        <button onClick={handleAddItem}>ADD ITEM</button>
    </div>
    <ul>
        {enteredItems.map((item, index) => (
            <Item index = {index} name={item}/>
        ))}
    </ul>
    </>
   );
};
export default ItemList;
