//Edhaya Login

import React, { useState } from "react";

import './login.css';
import { Link, useHistory } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
import Bengaluru from './bengaluru3.jpg';
const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();
    const employeeLogin = { username, password };
    const HandleSubmit = (e) => {
        e.preventDefault();
        EmployeeService.login(employeeLogin).then((response) => {

            if (response.data.position === "Admin") {
                history.push("/employees");

            }
            else {

                console.log("Logged in by : " + response.data.position);

                history.push("/view-employee/" + response.data.id);
            }
        }).catch(error => {
            console.log(error);
        })

    }

    return (
      

<>
    <div class="container">
        <div class="screen">
            <div class="screen__content">
                <form class="login">
                    <div class="login__field">
                        <i class="login__icon fas fa-user"></i>
                        <input type="text" class="login__input" placeholder="Employee Code" 
                        value={username}
 
                        onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div class="login__field">
                        <i class="login__icon fas fa-lock"></i>
                        <input type="password" class="login__input" placeholder="Password" value={password}
 
 onChange={(e) => setPassword(e.target.value)}/>
                    </div>
                    <button class="button login__submit">
                        <span class="button__text">Log In Now</span>
                        <i class="button__icon fas fa-chevron-right"></i>
                    </button>
                </form>
                <div class="social-login">
                    <h3>log in via</h3>
                    <div class="social-icons">
                        <a href="#" class="social-login__icon fab fa-instagram"></a>
                        <a href="#" class="social-login__icon fab fa-facebook"></a>
                        <a href="#" class="social-login__icon fab fa-twitter"></a>
                    </div>
                </div>
            </div>
            <div class="screen__background">
                <span class="screen__background__shape screen__background__shape4"></span>
                <span class="screen__background__shape screen__background__shape3"></span>
                <span class="screen__background__shape screen__background__shape2"></span>
                <span class="screen__background__shape screen__background__shape1"></span>
            </div>
        </div>
    </div>
    </>
        

);

};

export default Login;
