import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [username, setUsername] = useState('');
  const [storedUsername, setStoredUsername] = useState('');

  // Fetch the current username from the server when the component mounts
  useEffect(() => {
    axios.get('http://localhost:8080/api/username')
      .then(response => {
        setStoredUsername(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the username!', error);
      });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:8080/api/username', { username })
      .then(response => {
        setStoredUsername(response.data);
        setUsername('');
      })
      .catch(error => {
        console.error('There was an error saving the username!', error);
      });
  };

  return (
    <div className="App">
      <h1>Username Management</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <button type="submit">Submit</button>
      </form>
      <div>
        <h2>Current Username: {storedUsername}</h2>
      </div>
    </div>
  );
}

export default App;
