//New Login


import './Component.css';
import React, {useState, useEffect} from 'react'
import {Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';




const Login = () => {

const [employeeCode, setEmployeeCode] = useState('');
const [password, setPassword] = useState('');
const [genRoute,setGenRoute]=useState('');
const [id,setId]=useState('');
const employee={employeeCode,password};

const HandleSubmit=(e)=>
{

e.preventDefault();
const adminRoute="/employees";
const employeeRoute="/view-employee/";

EmployeeService.login(employee).then((response) => {
     
    setId(response.data.id)

    if (response.status===200)
        console.log(response.data.position);

    console.log("Logged in by : "+response.data.position);

    if(response.data.position==="Admin")
        setGenRoute(adminRoute)
    else
    setGenRoute(employeeRoute)
    

}).catch(error => {
    console.log(error);
});
}

return (
    <>
    <form>   
    <div id="main" className="container d-flex justify-content-center align-items-center">
    
     <div className="row mb-3">
 
         <label className="col-sm-4" className="form-control-sm form-label">EmployeeCode</label>
         <input className="col-sm-6" className="form-control-sm form-input"  
         type="text"
 
         value={employeeCode}
 
         onChange={(e) => setEmployeeCode(e.target.value)}  />
     
     </div>
     <div className="row mb-3">
         <label className="col-sm-4" className="form-control-sm form-label">Your Email ID</label>
         <input className="col-sm-6" className="form-control-sm form-input" 
     type="password"
 
     value={password}
 
     onChange={(e) => setPassword(e.target.value)}
 
     />
     </div>
     
     <div className="mt-3" >
     <Link className="btn btn-info"  to={`/employees`} >Admin</Link>
     <Link className="btn btn-info"  to={`/view-employee/${id}`} >Employee</Link>
     
     </div>
 </div>
 </form>  
 </>

);

};

export default Login;
